import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { blogActions } from '../_actions';

class AddBlog extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                title: '',
                content: ''
            },
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        this.setState({ submitted: true });
        const { user } = this.state;
        if (user.title && user.content) {
            this.props.addBlog(user);
        }
    }

    render() {
        const { registering  } = this.props;
        const { user, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Add Blog</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !user.title ? ' has-error' : '')}>
                        <label htmlFor="title">Title</label>
                        <input type="text" className="form-control" name="title" value={user.title} onChange={this.handleChange} />
                        {submitted && !user.title &&
                        <div className="help-block">Title is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !user.content ? ' has-error' : '')}>
                        <label htmlFor="content">Content</label>
                        <input type="text" className="form-control" name="content" value={user.content} onChange={this.handleChange} />
                        {submitted && !user.content &&
                        <div className="help-block">Content is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Add Blog</button>
                        {registering &&
                        <img src="data:image/gif;base64," />
                        }
                        <Link to="/" className="btn btn-link">Cancel</Link>
                    </div>
                </form>
            </div>
        );
    }
}

function mapState(state) {
    const { registering } = state.registration;
    return { registering };
}

const actionCreators = {
    addBlog: blogActions.addBlog
}

const connectedAddBlog = connect(mapState, actionCreators)(AddBlog);
export { connectedAddBlog as AddBlog };